#pragma once

#include <dbCommon/entities/Entities.h>

namespace database
{
   class IPluginEventLoggerRequester
   {
   public:
      //--------------------------------------------------------------
      /// \brief           Add new plugin event log entry
      /// \param [in]      pluginName   the plugin name
      /// \param [in]      pluginVersion   the plugin version
      /// \param [in]      eventType   the event type
      /// \param [in]      message   the message associated to event
      /// \return          harwareId generated by the database
      /// \throw           shared::exception::CEmptyResult if fails
      //--------------------------------------------------------------
      virtual int addEvent(const std::string& pluginName,
                           const std::string& pluginVersion,
                           const dbCommon::entities::EEventType& eventType,
                           const std::string& message = std::string()) = 0;

      //--------------------------------------------------------------
      /// \brief           Add new plugin event log entry
      /// \param [in]      pluginLogEntry   new plugin event log entry
      /// \return          eventId generated by the database
      /// \throw           shared::exception::CEmptyResult if fails
      //--------------------------------------------------------------
      virtual int addEvent(const dbCommon::entities::CPluginEventLogger& pluginLogEntry) = 0;

      //--------------------------------------------------------------
      /// \brief           Get the events history for a plugin
      /// \param [in]      pluginName   the plugin name
      /// \param [in]      pluginVersion   the plugin version
      /// \return          list of events
      /// \throw           shared::exception::CEmptyResult if fails
      //--------------------------------------------------------------
      virtual std::vector<boost::shared_ptr<dbCommon::entities::CPluginEventLogger>> getPluginEvents(const std::string& pluginName,
                                                                                                     const std::string& pluginVersion) = 0;

      //--------------------------------------------------------------
      /// \brief           Get the events history for a plugin
      /// \param [in]      pluginName   the plugin name
      /// \param [in]      pluginVersion   the plugin version
      /// \param [in]      fromDate   the date from which to take data
      /// \return          list of events
      /// \throw           shared::exception::CEmptyResult if fails
      //--------------------------------------------------------------
      virtual std::vector<boost::shared_ptr<dbCommon::entities::CPluginEventLogger>> getPluginEvents(const std::string& pluginName,
                                                                                                     const std::string& pluginVersion,
                                                                                                     const boost::posix_time::ptime& fromDate) = 0;

      //--------------------------------------------------------------
      /// \brief       Destructor
      //--------------------------------------------------------------
      virtual ~IPluginEventLoggerRequester()
      {
      }
   };
} //namespace database 


